<body>
    <section class="w-full px-6 antialiased bg-white">
    <div class="mx-auto max-w-7xl">
        <nav class="relative z-50 h-24 select-none" x-data="{ showMenu: false }">
            <div class="relative flex flex-wrap items-center justify-between h-24 mx-auto overflow-hidden font-medium md:overflow-visible lg:justify-center sm:px-4 md:px-2 lg:px-0">
                <div class="flex items-center justify-start w-1/4 h-full pr-4">
                    <a href="{{ url('/')}}" class="flex items-center text-lg py-4 space-x-2 font-extrabold text-gray-900 md:py-0">
                        <span class="text-[40px] mr-2">✪</span> {{ config('app.name', 'Laravel') }}
                    </a>
                </div>
